(9, 9, '_form', '<div class="row"><div class="col-md-5"><label>姓名（必填）</label></div><div class="col-md-7">[text* your-name 15/]</div></div>\n<div class="row"><div class="col-md-5"><label>邮箱（必填）</label></div><div class="col-md-7">[email* your-email 15/]</div></div>\n<div class="row"><div class="col-md-5"><label>电话</label></div><div class="col-md-7">[tel your-phone 15/]</div></div>\n<div class="row"><div class="col-md-5"><label>年龄</label></div><div class="col-md-7">[text your-age 15/]</div></div>\n<div class="row"><div class="col-md-5"><label>当前年级</label></div><div class="col-md-7">[text your-grade 15/]</div></div>\n<div class="row"><div class="col-md-5"><label>计划留澳时间</label></div><div class="col-md-7">[text your-plan 15/]</div></div>\n<div class="row"><div class="col-md-5"><label>其他要求</label></div><div class="col-md-7">[textarea other-requirement 17x5]</div></div>\n<div class="row"><div class="col-md-12 text-center">[submit "递交"]</div></div>'),
